import React, { useContext, useEffect, useState } from "react";
import { Pressable, View, StyleSheet, ScrollView, Image } from "react-native";
import { RootTabScreenProps } from "../../types";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import { Text } from "../../src/components/Themed";
import { Ionicons } from "@expo/vector-icons";
import NewChatButton from "../../src/components/newChat";
import { getColors } from "../../constants/Colors";
import Socket, { CreateSocketConnection } from "../../src/utils/socket";
import { Cipher, Decipher } from "../../middleware/crypto";
import { Userpic } from "react-native-userpic";
import EditChat from "../../src/components/chat/editChat";
import ChatContainer from '../../src/components/chat/chatContainer';

export default function Chats({ navigation }: RootTabScreenProps<"Chats">) {
  const [socket, setSocket] = useState();
  const [requestEditChat, setRequestEditChat] = useState(false);

  const { user, logged, updateUser } = useContext(UserContext);
  const { chats, updateChats } = useContext(ChatsContext);
  const colors = getColors();

  useEffect(() => {
    const socketConnection = CreateSocketConnection({ userId: user.id });
    setSocket(socketConnection);

    socketConnection.on("newChat", ({ newChat }) => {
      if (newChat) {
        chats.push(newChat);
        updateChats({ chats });
      }
    });
    socketConnection.on("receiveMessage", (msg) => {
      if (msg) {
        const thisChat = chats.filter((c) => c.user.id === msg.author.id)[0];
        if (thisChat) {
          if (thisChat.messages.some((m) => m.id === msg.id)) return;
          thisChat.messages.push(msg);
          updateChats({ chats });
        }
      }
    });
    socketConnection.on("readMessage", (p) => {
      const thisChat = chats.filter((c) => c.id === p.chat)[0];
      if (thisChat) {
        thisChat.messages.filter((m) => !m.read).map((m) => (m.read = true));
        updateChats({ chats });
      }
    });
  }, []);

  return (
    <View style={styles.container}>
      {!logged ? (
        <>{navigation.navigate("Welcome")}</>
      ) : (
        <ScrollView
          style={{ padding: 5 }}
          showsVerticalScrollIndicator={false}
          showsHorizontalScrollIndicator={false}
        >
        {!chats.length ? (
          <ChatContainer chat={new Socket.sendHalkMessage()} {...{ navigation, socket }} />
        ) : (
          <>
            {chats.map((chat, index) => (
              <ChatContainer {...{ chat, navigation, socket }} key={index} />
            ))}
          </>
        )}
        </ScrollView>
      )}
      <NewChatButton navigation={navigation} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  chat: {},
  leftContent: {
    flexDirection: "row",
  },
  userIcon: {
    width: 50,
    height: 50,
    borderRadius: 100,
    marginRight: 10,
  },
  username: {
    fontWeight: "bold",
  },
  messageContent: {
    fontSize: 13,
    opacity: 0.7,
    alignItems: "center",
  },
  rightContent: {
    alignItems: "center",
  },
  messageDate: {
    fontSize: 11,
    marginBottom: 3,
  },
  unread: {
    backgroundColor: "#2f95dc",
    borderRadius: 100,
    minWidth: 15,
    minHeight: 15,
    textAlign: "center",
    color: "white",
  },
});
