import { createContext, useEffect, useState } from 'react';
import AsyncStorage from "@react-native-async-storage/async-storage";

interface UserInterface {
    logged: boolean;
    user: {
        id: string;
        phone: string;
        profile: {
            name: string;
            username: string;
            avatar: string;
            bio: string;
        };
    };
    updateUser: Function;
};

export const UserContext = createContext<UserInterface>({} as UserInterface);

export const UserProvider = ({children}:any) => {
    const updateUser = (newUser:any) => {
        setUser({
            logged: newUser.logged,
            user: newUser.user,
            updateUser: updateUser,
        });
    }

    const initialValue: UserInterface = {
        logged: false,
        user: {
            id: '',
            phone: '',
            profile: {
                name: '',
                username: '',
                avatar: '',
                bio: '',
            },
        },
        updateUser: updateUser,
    };

    const [user, setUser] = useState<UserInterface>(initialValue);

    useEffect(() => {
        AsyncStorage.getItem('user').then((data) => {
            const dt = JSON.parse(data);

            if(dt) {
                console.log(dt.user);
            } else {
                AsyncStorage.setItem('user', JSON.stringify(initialValue));
            }
        });
    }, []);

    useEffect(() => {

    }, [user]);

    return (
        <UserContext.Provider value={user}>
            {children}
        </UserContext.Provider>
    )
}
