import { useState } from "react";
import { Text, View, Image, Pressable, StyleSheet, StatusBar } from "react-native";
import { RootStackScreenProps } from "../../types";
import Translate from "../../middleware/translate";

export default function Welcome({ navigation }: RootStackScreenProps<"Welcome">) {
  const [page, setPage] = useState(0);

  const pages = [
    {
      title: 'Welcome to Halk!',
      description: 'Halk',
      image: require('../../assets/images/favicon.png')
    },
  ];

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{pages[page].title}</Text>
      <Image source={pages[page].image} style={styles.image}/>
      <Text style={styles.description}>{pages[page].description}</Text>
      <Pressable style={styles.button} onPress={() => {
        if(pages[page+1]) {
          setPage(page+1);
        } else navigation.navigate('Register');
      }}>
        <Text>Continue</Text>
      </Pressable>
      <View style={styles.ballsContainer}>
        {pages.map((t, index) => (<Text style={styles.ball} key={index}> </Text>))}
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
    alignItems: 'center',
  },
  title: {
    fontWeight: 'bold',
  },
  image: {
    width: 300,
    height: 300,
  },
  description: {
    fontSize: 12,
  },
  button: {
    backgroundColor: '#2f95dc',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 50,
  },
  ballsContainer: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  ball: {
    width: 10,
    height: 10,
    backgroundColor: '#2f95dc',
    borderRadius: 100,
  },
});
