import io from 'socket.io-client';
import * as dotenv from 'dotenv';
import * as uuid from 'uuid';
dotenv.config();

export default class Socket {
  constructor() {
    this.socket = io("http://localhost:3000", {
      transports: ["websocket"],
    });
  }

  public async get() { return this.socket; }

  public sendMessage(text: string) {
    const message = {
      id: uuid.v4(),
      text,
      author: {},
    };

    this.socket.emit("message", message);
  }

  static getMessages(room: string) {
    if(!room) return;
    return this.socket.emit("getMessages", (msgs) => msgs);
  }

  static sendHalkMessage() {
    const halkUser = {
      id: '0',
      phone: '',
      profile: {
        name: 'Halk',
        username: 'Halk',
        avatar: require('../../assets/images/icon.png'),
        bio: 'Official account of the team Halk',
      },
    };

    const halkChat = {
      id: uuid.v4(),
      user: halkUser,
      messages: [
        {
          author: halkUser,
          content: 'Welcome to halk!',
          createdAt: `${new Date().getHours()}:${new Date().getMinutes()}`,
          id: uuid.v4(),
          read: false,
        },
      ],
    };

    return halkChat;
  }
}
