import { useState, useContext, useEffect } from "react";
import { View, StyleSheet, ScrollView, Pressable, Image } from "react-native";
import { RootStackScreenProps } from "../../types";
import { ChatsContext } from "../../src/contexts/chats";
import { TextInput, Text } from "../../src/components/Themed";
import { AntDesign } from "@expo/vector-icons";
import { getColors } from "../../constants/Colors";
import { searchUser } from "../../middleware/api";

export default function NewChat({
  navigation,
}: RootStackScreenProps<"NewChat">) {
  const [search, setSearch] = useState("");
  const [results, setResults] = useState([]);
  const { chats } = useContext(ChatsContext);
  const colors = getColors();

  useEffect(() => {
    if (search) {
      searchUser(search).then((data) => setResults(data));
    }
  }, [search]);

  return (
    <View style={styles.container}>
      <View style={styles.inputContainer}>
        <AntDesign name="arrowleft" size={25} color={colors.tint} />
        <TextInput
          style={[styles.input, { backgroundColor: colors.defaultColors.card }]}
          placeholder="Pesquisar"
          onChangeText={(value) => setSearch(value)}
          value={search}
        />
      </View>
      <ScrollView style={styles.results}>
        {!search ? (
          <View style={{ alignItems: "center", justifyContent: "center" }}>
            <Text style={{ fontSize: 12 }}>
              Search for a user by name, username or ID
            </Text>
          </View>
        ) : (
          <>
            {results.map((user, index) => (
              <Pressable
                style={({ pressed }) => [
                  styles.userContainer,
                  {
                    backgroundColor: pressed
                      ? colors.defaultColors.card
                      : "transparent",
                  },
                ]}
                key={index}
                onPress={() => console.log(user)}>
                <View style={styles.leftContent}>
                  <Image
                    source={
                      user.profile.avatar
                        ? { uri: user.profile.avatar }
                        : require("../../assets/images/userIcon.png")
                    }
                    style={styles.userIcon}
                  />
                  <View style={{ justifyContent: "center" }}>
                    <Text style={styles.username}>{user.profile.username}</Text>
                    <Text style={styles.name}>{user.profile.name}</Text>
                  </View>
                </View>
              </Pressable>
            ))}
          </>
        )}
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  inputContainer: {
    flexDirection: "row",
    alignItems: "center",
    padding: 10,
  },
  input: {
    padding: 5,
    borderRadius: 10,
    marginLeft: 10,
    width: "100%",
  },
  results: {
    flex: 1,
    marginHorizontal: 10,
  },
  userContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: 10,
    padding: 5,
  },
  leftContent: {
    flexDirection: "row",
  },
  userIcon: {
    width: 50,
    height: 50,
    borderRadius: 100,
    marginRight: 10,
  },
  username: {
    fontWeight: "bold",
  },
  name: {
    fontSize: 14,
  },
});
