import React, { useContext, useEffect } from "react";
import { Pressable, View, StyleSheet, ScrollView, Image } from "react-native";
import { RootTabScreenProps } from "../../types";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import { Text } from "../../src/components/Themed";
import { Ionicons } from "@expo/vector-icons";
import NewChatButton from "../../src/components/newChat";
import { getColors } from "../../constants/Colors";
import Socket, { CreateSocketConnection } from "../../src/utils/socket";

export default function Chats({ navigation }: RootTabScreenProps<"Chats">) {
  const { user, logged, updateUser } = useContext(UserContext);
  const { chats } = useContext(ChatsContext);
  const colors = getColors();

  useEffect(() => {
    const socketConnection = CreateSocketConnection();
    socketConnection.emit("connection", { userId: user.id });

    socketConnection.on("receiveMessage", (msg) => console.log(msg));
  }, [chats]);

  function ChatContainer({ chat, index = 1 }) {
    if (!chat.id) return;
    const lastMessage = chat.messages[chat.messages.length - 1];
    if(!lastMessage) return null;
    const unredMessages = chat.messages.filter((m) => !m.read);
    const messageTime = new Date(lastMessage.createdAt).toLocaleTimeString();

    return (
      <Pressable
        style={({ pressed }) => [
          styles.chat,
          {
            backgroundColor: pressed
              ? colors.defaultColors.card
              : "transparent",
          },
        ]}
        key={index}
        onPress={() =>
          navigation.navigate("Chat", {
            id: chat.id,
          })
        }
      >
        <View style={styles.leftContent}>
          <Image
            source={{ uri: chat.user.profile.avatar }}
            style={styles.userIcon}
          />
          <View style={{ justifyContent: "center" }}>
            <Text style={styles.username}>{chat.user.profile.name}</Text>
            {lastMessage.author.id === user.id ? (
              <View style={{flexDirection: 'row', alignItems: 'flex-end'}}>
                <Ionicons name="checkmark-done" size={20} color="gray" style={{marginRight: 2}} />
                <Text style={styles.messageContent}>
                  {lastMessage.content.length > 28
                    ? lastMessage.content.slice(0, 28) + "..."
                    : lastMessage.content}
                </Text>
              </View>
            ) : (
              <Text>
                {lastMessage.content.length > 30
                  ? lastMessage.content.slice(0, 30) + "..."
                  : lastMessage.content}
              </Text>
            )}
          </View>
        </View>
        <View style={styles.rightContent}>
          <Text style={styles.messageDate}>{messageTime.slice(0, 5)}</Text>
          {!unredMessages.length ? (
            <></>
          ) : (
            <Text style={styles.unread}>{unredMessages.length}</Text>
          )}
        </View>
      </Pressable>
    );
  }

  return (
    <View style={styles.container}>
      {!logged ? (
        <>{navigation.navigate("Welcome")}</>
      ) : (
        <ScrollView style={{ padding: 5 }}>
          {!chats.length ? (
            <ChatContainer chat={new Socket.sendHalkMessage()} />
          ) : (
            <>
              {chats.map((chat, index) => (
                <ChatContainer chat={chat} index={index} key={index} />
              ))}
            </>
          )}
        </ScrollView>
      )}
      <NewChatButton navigation={navigation} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  chat: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: 10,
    padding: 5,
  },
  leftContent: {
    flexDirection: "row",
  },
  userIcon: {
    width: 50,
    height: 50,
    borderRadius: 100,
    marginRight: 10,
  },
  username: {
    fontWeight: 'bold',
  },
  messageContent: {
    fontSize: 13,
    opacity: 0.7,
    alignItems: "center",
  },
  rightContent: {
    alignItems: "center",
  },
  messageDate: {
    fontSize: 11,
    marginBottom: 3,
  },
  unread: {
    backgroundColor: "#2f95dc",
    borderRadius: 100,
    minWidth: 15,
    minHeight: 15,
    textAlign: "center",
    color: "white",
  },
});
