import { useEffect, useState, useContext } from "react";
import {
  View,
  StyleSheet,
  StatusBar,
  TouchableOpacity,
  SectionList,
  ScrollView
} from "react-native";
import { RootTabScreenProps } from "../../types";
import { MaterialIcons, FontAwesome5 } from "@expo/vector-icons";
import { Text, TextInput } from "../../src/components/Themed";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import { Userpic } from "react-native-userpic";
import { getColors } from "../../constants/Colors";

const badgeProps = {
  size: 50,
  borderRadius: 50,
  animate: true,
};

export default function Menu({ navigation }: RootTabScreenProps<"Menu">) {
  const { user } = useContext(UserContext);
  const { chats } = useContext(ChatsContext);
  const [calls, setCalls] = useState([]);
  const colors = getColors();

  return (
    <View style={styles.container}>
    <View style={styles.topContainer}>
      <TouchableOpacity style={styles.profileContainer}>
        <Userpic
          size={50}
          name={user.profile.name}
          source={{ uri: user.profile.avatar }}
          colorize={true}
          borderRadius="50%"
          badge={true}
          badgeColor={"#2f95dc"}
          badgePosition={"bottom-right"}
          badgeProps={badgeProps}
          style={{ marginRight: 10 }}
        />

        <View>
          <Text style={{ fontWeight: "bold" }}>{user.profile.name}</Text>
          <Text style={{ fontSize: 13 }}>{user.profile.username}</Text>
        </View>
      </TouchableOpacity>

      <TouchableOpacity>
        <FontAwesome5 name="exchange-alt" size={26} color={colors.tint} />
      </TouchableOpacity>
    </View>

      <ScrollView></ScrollView>
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
    marginHorizontal: 10,
  },
  topContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginTop: 10,
  },
  profileContainer: {
    flexDirection: "row",
    alignItems: "center",
    padding: 5,
  },
});
