import io from "socket.io-client";
import * as dotenv from "dotenv";
import * as uuid from "uuid";
dotenv.config();

class SocketController {
  constructor(socket) {
    this.socket = socket;
  }

  public getSocket() {
    return this.socket;
  }

  public joinRoom(room: string) {
    if (!room) return;
    this.socket.emit("joinRoom", room);
  }

  public async getMessages(room) {}

  public sendMessage(text: string) {
    const message = {
      id: uuid.v4(),
      text,
      author: {},
    };

    this.socket.on("message", (msg) => console.log(msg));
    this.socket.emit("message", message);
  }

  public async getMessages(room: string) {}

  static sendHalkMessage() {
    const halkUser = {
      id: "0",
      phone: "",
      profile: {
        name: "Halk",
        username: "Halk",
        avatar: require("../../assets/images/icon.png"),
        bio: "Official account of the team Halk",
      },
    };

    const halkChat = {
      id: "a384b2bc-1a3b-49d3-bdf5-1be72350c494",
      user: halkUser,
      messages: [
        {
          author: halkUser,
          content: "Welcome to halk!",
          createdAt: `${new Date().getHours()}:${new Date().getMinutes()}`,
          id: uuid.v4(),
          read: false,
        },
      ],
    };

    return halkChat;
  }
}

export default function CreateSocketConnection() {
  const socket = io("http://localhost:3000", {
    transports: ["websocket"],
  });

  return new SocketController(io);
}
