import { useEffect, useState, useContext } from "react";
import { View, StyleSheet, StatusBar, ScrollView, TouchableOpacity } from "react-native";
import { RootTabScreenProps } from "../../types";
import { Text, TextInput } from "../../src/components/Themed";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import { Userpic } from "react-native-userpic";
import { FontAwesome, Entypo, EvilIcons } from "@expo/vector-icons";
import { getColors } from "../../constants/Colors";

const badgeProps = {
  size: 50,
  borderRadius: 50,
  animate: true,
};

export default function Status({ navigation }: RootTabScreenProps<"Status">) {
  const { user } = useContext(UserContext);
  const { chats } = useContext(ChatsContext);
  const [status, setStatus] = useState([]);
  const colors = getColors();

  useEffect(() => {
    //get status
    if(!status.length) {
      setStatus((current) => [...current, {
        author: {
          profile: {
            name: "Halk",
          },
        },
        type: "text",
        content: "Welcome to Halk!",
      }]);
    }
  }, []);

  return (
    <View style={styles.container}>
      <View style={styles.userContainer}>
        <TouchableOpacity style={styles.rowContainer} onPress={() => console.log("open camera")}>
          <Userpic
            size={50}
            name={user.profile.name}
            source={{ uri: user.profile.avatar }}
            colorize={true}
            borderRadius="50%"
            badge={true}
            badgeColor={"#2f95dc"}
            badgePosition={"bottom-right"}
            badgeProps={badgeProps}
            style={{ marginRight: 10 }}
          />
          <View>
            <Text style={{ fontWeight: "bold" }}>Meu status</Text>
            <Text style={{ fontSize: 13 }}>
              Toque para atualizar seu status
            </Text>
          </View>
        </TouchableOpacity>
        <View style={styles.rowContainer}>
          <TouchableOpacity style={styles.blueButton} onPress={() => console.log("open camera two")}>
            <Entypo name="camera" size={22} color="white" />
          </TouchableOpacity>
          <TouchableOpacity style={styles.blueButton} onPress={() => console.log("open text")}>
            <FontAwesome name="pencil" size={22} color="white" />
          </TouchableOpacity>
        </View>
      </View>
      <View
        style={[
          styles.searchContainer,
          { borderBottomColor: colors.defaultColors.border },
        ]}
      >
        <TextInput
          placeholder="Pesquisar"
          style={[
            styles.searchInput,
            { backgroundColor: colors.defaultColors.card },
          ]}
        />
      </View>

      <ScrollView style={styles.statusContainer}>
        {status.map((stts) => {
          console.log(stts);
          if(stts.type === "text") {
            return (<View style={styles.statusBlock}>
              <View style={styles.statusLines}>

              <Userpic
            size={50}
            name={stts.content}
            colorize={true}
            borderRadius="50%"
            style={{ marginRight: 10 }}
          />
              </View>
              <View>
                <Text>{stts.author.profile.name}</Text>
                <Text>Agora mesmo</Text>
              </View>
            </View>);
          } else {
            return (<View>
              <Userpic
            size={50}
            source={{ uri: status.content }}
            colorize={true}
            style={{ marginRight: 10 }}
          />
            </View>)
          }
        })}
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
    margin: 10,
  },
  userContainer: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  statusContainer: {},
  rowContainer: {
    flexDirection: "row",
    alignItems: "center",
  },
  blueButton: {
    ṕadding: 10,
    borderRadius: 100,
    backgroundColor: "#2f95dc",
    width: 35,
    height: 35,
    alignItems: "center",
    justifyContent: "center",
    marginRight: 5,
  },
  searchContainer: {
    borderBottomWidth: 1,
    padding: 10,
    margintop: 10,
  },
  searchInput: {
    borderRadius: 10,
    width: "100%",
    height: 30,
    padding: 10,
  },
  statusBlock: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  statusLines: {
    borderColor: "#2f95dc",
    borderWidth: 1,
  },
});
