import React, { Component, ReactElement, useState } from "react";
import { StyleSheet, TouchableOpacity, View } from "react-native";
import { Text } from "../Themed";
import { Userpic } from "react-native-userpic";

class ChatContainer extends Component {
  badgeProps = {
    size: 25,
    borderRadius: 50,
    animate: true,
  };

  constructor(props) {
    super(props);

    console.log(props.socket);

    this.chat = props.chat;
    this.navigation = props.navigation;

    this.isOnline = false;

  }

  EditChat(): ReactElement<any> {
    return (
      <></>
    );
  }

  render(): ReactElement<any> {
    return <></>;
    const lastMessage = this.chat.messages[chat.messages.length - 1];
    if (!lastMessage) return null;
    const unredMessages = this.chat.messages.filter((m) => m.read === false);
    const messageTime = new Date(lastMessage.createdAt).toLocaleTimeString();
    const [isOnline, setIsOnline] = useState(false);

    if (socket) {
      socket.emit("verifyIfUserIsOnline", { userId: chat.user.id });
      socket.on("receiveIfUserIsOnline", (callback) => setIsOnline(callback));
    }

    let content = chat.key
      ? Decipher(lastMessage.content, chat.key)
      : "Nova mensagem";

    return (
      <TouchableOpacity
        style={styles.chatContainer}
        onPress={() => {}}
        onLongPress={() => {}}
      >
      <this.EditChat/>
      <View style={{ flexDirection: "row" }}>

      <Userpic
        size={40}
        name={this.chat.user.profile.name}
        source={{ uri: this.chat.user.profile.avatar }}
        colorize={true}
        borderRadius="50%"
        badge={true}
        badgeColor={this.isOnline ? "#00ff0d" : "#919191"}
        badgePosition={"bottom-right"}
        badgeProps={this.badgeProps}
        style={{ marginRight: 10 }}
      />
      </View>
      <View style={{ justifyContent: "center" }}>
        <Text style={styles.username}>{this.chat.user.profile.name}</Text>
        {lastMessage.author.id === user.id ? (
          <View style={{ flexDirection: "row", alignItems: "center" }}>
            <Ionicons
              name="checkmark-done"
              size={20}
              color={lastMessage.read ? "#2f95dc" : "gray"}
              style={{ marginRight: 2 }}
            />
            <Text style={styles.messageContent}>
              {content.length > 28 ? content.slice(0, 28) + "..." : content}
            </Text>
          </View>
        ) : (
          <Text>
            {content.length > 30 ? content.slice(0, 30) + "..." : content}
          </Text>
        )}
      </View>
      </TouchableOpacity>
    );
  }
}

const styles = StyleSheet.create({
  chatContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: 10,
    padding: 5,
  },
});

export default ChatContainer;
