import React, { Component, ReactElement, useState } from "react";
import { StyleSheet, TouchableOpacity, View, ScrollView } from "react-native";
import { Text } from "./Themed";
import { Userpic } from "react-native-userpic";
import CreateSocketConnectio from "../utils/socket";
import { Cipher, Decipher } from "../../middleware/crypto";
import { Ionicons } from "@expo/vector-icons";
import Modal from "react-native-modal";

class Chat extends Component {
  public scrollViewRef: React.RefObject<ScrollView> = React.createRef();
  public badgeProps = {
    size: 25,
    borderRadius: 50,
    animate: true,
  };

  constructor(props) {
    super(props);
    this.state = {
      visible: false,
      isOnline: false,
      scrollOffset: null,
    };

    this.user = props.user;
    this.chats = props.chats;
    this.updateChats = props.updateChats;

    this.chat = props.chat;
    this.navigation = props.navigation;
    this.socket = props.socket;
  }

  handleOnScroll(event) {
    this.setState({ scrollOffset: event.nativeEvent.contentOffset.y });
  }

  handleScrollTo(p) {
    console.log(this.scrollViewRef, p);
  }

  open = () => this.setState({ visible: true } as any);
  close = () => this.setState({ visible: false } as any);
  isVisible = () => this.state.visible;

  ModalChat(): ReactElement<any> {
    return <Text>pika</Text>
  }

  render(): ReactElement<any> {
    return <Text>cu</Text>;
  }
}

const styles = StyleSheet.create({});

export default Chat;
