import * as React from "react";
import { Text } from "../src/components/Themed";
import { View, StyleSheet, Pressable } from "react-native";
import { MaterialIcons } from "@expo/vector-icons";
import RNRestart from "react-native-restart";

export default class ErrorBoundary extends React.Component {
  private errorInfo = "Tivemos um erro em nosso sistema, tente novamente.";

  constructor(props) {
    super(props);
    this.state = { hasError: false };
    this.colors = props.colors;
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    this.errorInfo = errorInfo;
  }

  render() {
    return (
      <View
        style={[styles.container, { backgroundColor: this.colors.background }]}
      >
        <MaterialIcons name="error" size={100} color={this.colors.tint} />
        <Text style={{ fontSize: 25, fontWeight: "bold" }}>Oops!</Text>
        <Text style={{ fontSize: 16, textAlign: "center" }}>
          Houve um erro em nossos sistemas, por favor reinicie o aplicativo para
          corrigir o erro.
        </Text>
        <Pressable style={styles.button} onPress={() => RNRestart.Restart()}>
          <Text style={{ fontSize: 16, color: "white", fontWeight: "bold" }}>
            Tentar novamente
          </Text>
        </Pressable>
      </View>
    );

    if (this.state.hasError) {
    }
    return this.props.children;
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  button: {
    backgroundColor: "#2f95dc",
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 50,
    marginTop: 50,
  },
});
