import { useState } from "react";
import { Image, StatusBar, StyleSheet, View } from "react-native";
import { RootStackScreenProps } from "../../types";
import { Text } from "../../src/components/Themed";
import {
  CodeField,
  Cursor,
  useBlurOnFulfill,
  useClearByFocusCell,
} from 'react-native-confirmation-code-field'

export default function ReceiveCode({ navigation }: RootStackScreenProps<"ReceiveCode">) {
  const [value, setValue] = useState('');
  const ref = useBlurOnFulfill({value, cellCount: 6});
  const [props, getCellOnLayoutHandler] = useClearByFocusCell({
    value,
    setValue,
  });

  return (
    <View style={styles.container}>
      <Image source={require('../../assets/images/verificationCode.png')} style={styles.image}/>
      <Text style={styles.description}>Insíra abaixo com o código de verificação enviado para o seu número</Text>
      <View style={styles.codeContainer}>
      <CodeField
        ref={ref}
        {...props}
        value={value}
        onChangeText={setValue}
        cellCount={6}
        rootStyle={styles.codeFieldRoot}
        keyboardType="number-pad"
        textContentType="oneTimeCode"
        renderCell={({index, symbol, isFocused}) => (
          <Text
            key={index}
            style={[styles.cell, isFocused && styles.focusCell]}
            onLayout={getCellOnLayoutHandler(index)}>
            {symbol || (isFocused ? <Cursor /> : null)}
          </Text>
        )}
      />
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
    alignItems: 'center',
  },
  image: {
    width: 100,
    height: 100,
    marginTop: 100,
  },
  codeFieldRoot: {marginTop: 20},
  cell: {
    width: 40,
    height: 50,
    lineHeight: 50,
    fontSize: 24,
    borderWidth: 2,
    borderColor: '#00000030',
    textAlign: 'center',
    borderRadius: 5,
    marginRight: 5,
  },
  focusCell: {
    borderColor: '#00000030',
  },
  codeContainer: {
    marginHorizontal: 10,
  },
  description: {
    fontSize: 12,
    textAlign: 'center',
    width: 290,
    opacity: 0.9,
  }
});
