import ImgurClient from "imgur";

const api_link = "http://localhost:3000";

export const uploadImage = async (image: any) => {
  const clientId = process.env.IMGUR_ID;
  const clientSecret = process.env.IMGUR_SECRET;

  const client = new ImgurClient({
    clientId: "aca6d2502f5bfd8",
  });

  const formData = new FormData();
  formData.append('image', image.split(',')[1]);

  console.log(formData);

  const data = await client.upload({
    image: formData,
    type: 'base64',
  });

  console.log(data.data);
};

export const createProfile = async (user: any) => {
  let getUserIconURL = await uploadImage(user.profile.avatar);

  user.profile.avatar = getUserIconURL;

  return;

  return fetch(`${api_link}/register/createUser`, {
    method: "POST",
    headers: {
      "Content-type": "application/json;charset=UTF-8",
    },
    body: JSON.stringify(user),
  })
    .then((res) => res.json())
    .then((res) => res);
};
