import React, { useEffect, useState } from 'react';
import { Button, StatusBar, StyleSheet, View } from "react-native";
import { RootStackScreenProps } from "../../types";
import * as uuid from 'uuid';
import io from 'socket.io-client';
import { TextInput, Text } from '../../src/components/Themed';

interface Message {
  id: string;
  name: string;
  text: string;
}

interface Payload {
  name: string;
  text: string;
}

export default function Chat({ navigation }: RootStackScreenProps<"Chat">) {
  const [title] = useState('Chat Web');
  const [name, setName] = useState('');
  const [text, setText] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);

  useEffect(() => {
    const socket = io('http://localhost:3000');
    console.log(socket);
  }, [messages, name, text]);

  function validateInput() {}

  function sendMessage() {}

  return (
    <View style={styles.container}>
      <TextInput value={name} onChangeText={(value) => setName(value)} placeholder="enter your name"/>
      <View style={styles.messageCard}>
      {messages.map(message => {
            if (message.name === name) {
              return (
                <Text>
                  {message.name} {message.text}
                </Text>
              );
            }

            return (
              <Text>
                {message.name} {message.text}
              </Text>
            );
          })}
      </View>
      <TextInput value={text} onChangeText={(value) => setText(value)} placeholder='Send a message'/>
      <Button title='Send' onPress={() => sendMessage()}/>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  messageCard: {
    borderWidth: 1,
    borderColor: '#222',
  },
  myMessage: {
    backgroundColor: 'blue',
  },
  otherMessage: {
    backgroundColor: 'gray',
  },
});
