import React, { useEffect, useState, useContext } from "react";
import { Button, StatusBar, StyleSheet, View } from "react-native";
import { AntDesign, Ionicons, Feather, FontAwesome } from "@expo/vector-icons";
import { RootStackScreenProps } from "../../types";
import { TextInput, Text } from "../../src/components/Themed";
import Socket from "../../src/utils/socket";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";

export default function Chat({ navigation, route }: RootStackScreenProps<"Chat">) {
  const [chat, setChat] = useState();
  const [socket, setSocket] = useState();
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [value, setValue] = useState("");
  const [name, setName] = useState("");
  const [ready, setReady] = useState(false);
  const [otherUser, setOtherUser] = useState();

  const { user } = useContext(UserContext);
  const { chats } =  useContext(ChatsContext);

  useEffect(() => {
    const chatId = route.params.id;

    if(chatId) {
      if(chatId === '0' || !chats.length) {
        setChat({
          id: '0',
          user: {
            id: '00000000000000000000000',
            phone: '000000000000000',
            profile: {
              name: 'Halk',
              username: 'halk',
              avatar: '',
              bio: '',
            }
          },
          messages: [
            {
              content: 'Welcome to halk',
              author: {
                id: '00000000000000000000000',
                phone: '000000000000000',
                profile: {
                  name: 'Halk',
                  username: 'halk',
                  avatar: '',
                  bio: '',
                }
              },
            }
          ],
        });
      } else {
        const thisChat = chats.filter(c => c.id === chatId);
        if(thisChat) {
          setChat(thisChat);
        } else navigation.navigate("Root");
      }
    } else navigation.navigate("Root");
  }, [user]);

  useEffect(() => console.log(chat), [chat]);

  return (
    <View style={styles.container}>
    {!chat ? <></> : (<>

      <View style={styles.topContainer}>

      <View>
      <AntDesign name="arrowleft" size={24} color="black" />

      <View>
      <Image source={chat.user.profile.avatar ? {uri: chat.user.profile.avatar} : require('../../assets/images/userIcon.png')}/>
      <Text>{chat.user.profile.name}</Text>
      </View>
      </View>

      <View>
      <Ionicons name="call" size={24} color="black" />
      <FontAwesome name="video-camera" size={24} color="black" />
      <Feather name="more-vertical" size={24} color="black" />
      </View>

      </View>

      <View style={styles.messageContainer}></View>

      <View style={styles.bottomBar}></View>




      </>)}

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  topContainer: {
    backgroundColor: '#222',
  },
  leftContent: {},
  rightContent: {},
  otherUserInfo: {},
  otherUserName: {},
  messageContainer: {},
  bottomBar: {},
  messageCard: {
    borderWidth: 1,
    borderColor: "#222",
  },
  myMessage: {
    backgroundColor: "blue",
  },
  otherMessage: {
    backgroundColor: "gray",
  },
});
