import React, { useEffect, useState, useContext, useRef } from "react";
import { Button, StatusBar, StyleSheet, View, Image, Pressable, ScrollView } from "react-native";
import { AntDesign, Ionicons, Feather, FontAwesome, MaterialIcons, Fontisto } from "@expo/vector-icons";
import { RootStackScreenProps } from "../../types";
import { TextInput, Text } from "../../src/components/Themed";
import Socket from "../../src/utils/socket";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";

export default function Chat({ navigation, route }: RootStackScreenProps<"Chat">) {
  const [chat, setChat] = useState();
  const [socket, setSocket] = useState();
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [value, setValue] = useState("");
  const [name, setName] = useState("");
  const [ready, setReady] = useState(false);
  const [otherUser, setOtherUser] = useState();

const scrollViewRef = useRef();

  const { user } = useContext(UserContext);
  const { chats } =  useContext(ChatsContext);

  useEffect(() => {
    const chatId = route.params.id;

    if(chatId) {
      if(chatId === '0' || !chats.length) {
        setChat({
          id: '0',
          user: {
            id: '00000000000000000000000',
            phone: '000000000000000',
            profile: {
              name: 'Halk',
              username: 'halk',
              avatar: '',
              bio: '',
            }
          },
          messages: [
            {
              content: 'Welcome to halk',
              createdAt: `${new Date().getHours()}:${new Date().getMinutes()}`,
              author: {
                id: '00000000000000000000000',
                phone: '000000000000000',
                profile: {
                  name: 'Halk',
                  username: 'halk',
                  avatar: '',
                  bio: '',
                }
              },
            },
          ],
        });
      } else {
        const thisChat = chats.filter(c => c.id === chatId);
        if(thisChat) {
          setChat(thisChat);
        } else navigation.navigate("Root");
      }
    } else navigation.navigate("Root");
  }, [user]);

  useEffect(() => console.log(chat), [chat]);

  return (
    <View style={styles.container}>
    {!chat ? <></> : (<>

      <View style={styles.topContainer}>

      <View style={styles.topContainerLeft}>
      <Pressable style={({ pressed }) => [
        styles.backButton,
        { backgroundColor: pressed ? "#333" : "transparent" },
      ]}
      onPress={() => navigation.navigate("Root")}
      >
      <AntDesign name="arrowleft" size={25} color="white" />
      </Pressable>

      <Pressable onPress={() => {}} style={styles.userProfile}>
      <Image source={chat.user.profile.avatar ? {uri: chat.user.profile.avatar} : require('../../assets/images/userIcon.png')} style={styles.userIcon}/>
      <Text style={{fontWeight: 'bold'}}>{chat.user.profile.name}</Text>
      </Pressable>
      </View>

      <View style={styles.topContainerRight}>
      <Pressable style={({ pressed }) => [
        styles.backButton,
        { backgroundColor: pressed ? "#333" : "transparent" },
      ]} onPress={() => {}}>
<Ionicons name="call" size={24} color="white" />
      </Pressable>
      <Pressable style={({ pressed }) => [
        styles.backButton,
        { backgroundColor: pressed ? "#333" : "transparent" },
      ]} onPress={() => {}}>
<FontAwesome name="video-camera" size={24} color="white" />
      </Pressable>
      <Pressable style={({ pressed }) => [
        styles.backButton,
        { backgroundColor: pressed ? "#333" : "transparent" },
      ]} onPress={() => {}}>
<Feather name="more-vertical" size={24} color="white" />
      </Pressable>
      </View>

      </View>

      <ScrollView style={styles.messageContainer} ref={scrollViewRef}
      onContentSizeChange={() => scrollViewRef.current.scrollToEnd({ animated: true })}>
      {chat.messages.map((message, index) => {
        return (
          <Pressable style={[styles.message, message.author.id === user.id ? styles.myMessage : styles.otherMessage]} key={index}>
          <Text>{message.content}</Text>
          <Text style={styles.messageCreatedAt}>{message.createdAt}</Text>
          </Pressable>
        );
      })}
      </ScrollView>

      <View style={styles.bottomContainer}>
      <Pressable style={({ pressed }) => [
        styles.openCamera,
        { opacity: pressed ? 0.5 : 1 },
      ]} onPress={() => {}}>
      <Fontisto name="camera" size={18} color="white" />
      </Pressable>
      <TextInput style={styles.inputMessage} placeholder="Mensagem..."/>
      <View style={styles.othersMedias}>
      <Pressable style={({ pressed }) => [
        {marginRight: 5},
        { opacity: pressed ? 0.5 : 1 },
      ]} onPress={() => {}}>
      <MaterialIcons name="keyboard-voice" size={24} color="white" />
      </Pressable>
      <Pressable style={({ pressed }) => [
        { opacity: pressed ? 0.5 : 1 },
      ]} onPress={() => {}}>
      <Ionicons name="ios-add-circle-outline" size={24} color="white" />
      </Pressable>
      </View>
      </View>
      </>)}

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  topContainer: {
    backgroundColor: '#222',
    flexDirection: 'row',
    alignItems: 'center',
    padding: 5,
    justifyContent: 'space-between',
  },
  topContainerLeft: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  topContainerRight: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  otherUserInfo: {},
  otherUserName: {},
  messageContainer: {
    flex: 1,
    margin: 10,
    flexDirection: 'column-reverse',
    marginBottom: 50,
  },
  bottomContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    position: 'absolute',
    bottom: 0,
    backgroundColor: '#222',
    padding: 5,
    borderRadius: 50,
    margin: 10,
  },
  messageCard: {
    borderWidth: 1,
    borderColor: "#222",
  },
  myMessage: {
    backgroundColor: "blue",
    justifyContent: 'flex-end',
    alignItems: 'flex-end',
  },
  otherMessage: {
    backgroundColor: "gray",
    maxWidth: '75%',
    width: 'fit-content',
  },
  userIcon: {
    width: 40,
    height: 40,
    borderRadius: 100,
    marginHorizontal: 5,
  },
  userProfile: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  backButton: {
    padding: 5,
    borderRadius: 100,
  },
  openCamera: {
    backgroundColor: "#2f95dc",
    padding: 5,
    borderRadius: 100,
    marginRight: 10,
  },
  othersMedias: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  inputMessage: {},
  message: {
    padding: 7,
    borderRadius: 20,
    flexDirection: 'row',
    marginBottom: 10,
    alignItems: 'flex-end',
    flexWrap: 'wrap',
  },
  messageCreatedAt: {
    fontSize: 10,
    marginLeft: 5,
  },
});
