import { useState, useContext } from "react";
import { View, StyleSheet } from "react-native";
import { RootStackScreenProps } from "../../types";
import { ChatsContext } from '../../src/contexts/chats';
import { TextInput, Text } from '../../src/components/Themed';
import {
  AntDesign,
} from "@expo/vector-icons";
import { getColors } from '../../constants/Colors';

export default function NewChat({ navigation }: RootStackScreenProps<"NewChat">) {
  const [search, setSearch] = useState('');
  const { chats } = useContext(ChatsContext);
  const colors = getColors();

  return (
    <View style={styles.container}>
      <View style={styles.inputContainer}>
        <AntDesign name="arrowleft" size={25} color={colors.tint} />
        <TextInput style={[styles.input, {backgroundColor: colors.defaultColors.card}]} placeholder='Pesquisar'/>
      </View>
      <View></View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 10,
  },
  input: {
    padding: 5,
    borderRadius: 10,
    marginLeft: 10,
    width: '100%',
  },
});
