import {} from "react";
import { Image, Pressable, StatusBar, StyleSheet, View } from "react-native";
import { RootStackScreenProps } from "../../types";
import { Text, TextInput } from "../../src/components/Themed";

export default function CreateProfile({
  navigation,
}: RootStackScreenProps<"CreateProfile">) {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Create your profile</Text>
      <View style={styles.primaryDiv}>
        <View style={styles.iconContainer}>
        <Image source={require('../../assets/images/userIcon.png')} style={styles.userIcon}/>
        <Text style={{fontSize: 10}}>Choose a icon</Text>
        </View>
        <View style={styles.inputsContainer}>
          <View style={styles.inputContainer}>
            <Text style={styles.inputTitle}>Name</Text>
          <TextInput style={styles.nameInput}/>
          </View>
          <View style={styles.inputContainer}>
            <Text style={styles.inputTitle}>Username</Text>
          <TextInput style={styles.nameInput}/>
          </View>
        </View>
      </View>
      <View style={styles.secondaryDiv}>
        <Text style={styles.inputTitle}>Bio</Text>
        <TextInput style={styles.bioInput}/>
      </View>

      <Pressable style={styles.nextButton}>
        <Text style={{color: 'white'}}>Continue</Text>
      </Pressable>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
    alignItem: "center",
  },
  title: {
    fontWeight: "bold",
    textAlign: 'center',
    margin: 10,
  }, 
  primaryDiv: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  secondaryDiv: {
    margin: 10,
  },
  userIcon: {
    width: 100,
    height: 100,
    marginBottom: 5,
  },
  iconContainer: {
    margin: 10,
    alignItems: 'center',
  },
  inputsContainer: {},
  inputContainer: {},
  inputTitle: {
    fontSize: 10,
  },
  nameInput: {
    borderBottomColor: '#222',
    borderBottomWidth: 1,
    paddingVertical: 3,
    marginBottom: 10,
  },
  bioInput: {
    borderWidth: 1,
    borderColor: '#222',
    borderRadius: 10,
    marginTop: 5,
    height: 70,
  },
  nextButton: {
    backgroundColor: '#2f95dc',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 50,
    position: 'absolute',
    bottom: 10,
  },
});
