import { useEffect, useState } from "react";
import {
  View,
  StyleSheet,
  ScrollView,
  ImageBackground,
  TouchableOpacity,
} from "react-native";
import { RootStackScreenProps } from "../../types";
import { searchUser } from "../../middleware/api";
import { getColors } from "../../constants/Colors";
import {
  AntDesign,
  Ionicons,
  Feather,
  FontAwesome,
  MaterialIcons,
  Fontisto,
} from "@expo/vector-icons";
import { Text } from '../../src/components/Themed';

export default function Profile({
  navigation,
  route,
}: RootStackScreenProps<"Profile">) {
  const [user, setUser] = useState();
  const colors = getColors();

  useEffect(() => {
    const userId = route.params.id;
    if (userId) {
      searchUser(userId).then((data) => {
        if (data[0]) {
          setUser(data[0]);
        } else navigation.goBack();
      });
    } else navigation.goBack();
  }, []);

  return (
    <>
      {!user ? (
        <></>
      ) : (
        <ScrollView style={styles.container}>
          <ImageBackground
            source={
              user.profile.avatar
                ? { uri: user.profile.avatar }
                : require("../../assets/images/userIcon.png")
            }
            resizeMode="cover"
            style={styles.topContainer}
          >
            <View style={styles.topContainerIcons}>
              <TouchableOpacity onPress={() => navigation.goBack()}>
                <AntDesign name="arrowleft" size={25} color={colors.tint} />
              </TouchableOpacity>

              <View style={styles.topContainerIconsRight}>
                <TouchableOpacity>
                  <Ionicons name="call" size={24} color={colors.tint} />
                </TouchableOpacity>
                <TouchableOpacity>
                  <FontAwesome
                    name="video-camera"
                    size={24}
                    color={colors.tint}
                  />
                </TouchableOpacity>
                <TouchableOpacity>
                  <Feather name="more-vertical" size={24} color={colors.tint} />
                </TouchableOpacity>
              </View>
            </View>
            <View style={styles.topContainerInfo}>
              <Text>{user.profile.name}</Text>
              <Text>{user.profile.username}</Text>
            </View>
          </ImageBackground>

          <View style={styles.userInfoContainer}></View>

          <View style={styles.mediaContainer}></View>
        </ScrollView>
      )}
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  topContainer: {},
  topContainerIcons: {},
  topContainerIconsRight: {},
  userInfoContainer: {},
  mediaContainer: {},
});
