import { Component, createRef } from "react";
import { ScrollView, View, StyleSheet, StatusBar } from "react-native";

class MessagesContainer extends Component {
  private scrollViewRef = createRef();

  constructor(props) {
    super(props);
    this.user = props.user;
    this.chats = props.chats;
    this.updateChats = props.updateChats;
    this.chat = props.chat;
  }

  ModalMessage() {}

  render() {
    return (<ScrollView style={styles.messageContainer}
        ref={this.scrollViewRef}
        showsVerticalScrollIndicator={false}
        showsHorizontalScrollIndicator={false}
        onContentSizeChange={() =>
          this.scrollViewRef.current.scrollToEnd({ animated: true })
        }>
          {this.chats.filter((c) => c.id === this.chat.id).map((c) => c.messages).map((message, index) => {
            console.log(message);
          })}
        </ScrollView>)
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  topContainer: {
    flexDirection: "row",
    alignItems: "center",
    padding: 5,
    justifyContent: "space-between",
  },
  topContainerLeft: {
    flexDirection: "row",
    alignItems: "center",
  },
  topContainerRight: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  messageContainer: {
    flex: 1,
    marginHorizontal: 10,
    flexDirection: "column-reverse",
    marginBottom: 55,
  },
  bottomContainer: {
    flexDirection: "row",
    alignItems: "center",
    position: "absolute",
    backgroundColor: "rgb(18, 18, 18)",
    justifyContent: "space-between",
    borderRadius: 50,
    bottom: 0,
    left: 0,
    right: 0,
    padding: 5,
    margin: 5,
  },
  messageCard: {
    borderWidth: 1,
    borderColor: "#222",
  },
  myMessage: {
    justifyContent: "flex-end",
    flexDirection: "revert",
  },
  otherMessage: {
    width: "fit-content",
    maxWidth: "75%",
  },
  userIcon: {
    width: 40,
    height: 40,
    borderRadius: 100,
    marginHorizontal: 5,
  },
  userProfile: {
    flexDirection: "row",
    alignItems: "center",
  },
  button: {
    padding: 5,
  },
  blueButton: {
    backgroundColor: "#2f95dc",
    padding: 8,
    borderRadius: 100,
  },
  othersMedias: {
    flexDirection: "row",
    alignItems: "center",
  },
  inputMessage: {
    paddingVertical: 8,
    width: "100%",
    marginLeft: 10,
  },
  message: {
    padding: 7,
    borderRadius: 20,
    flexDirection: "row",
    marginBottom: 2,
    alignItems: "baseline",
    flexWrap: "wrap",
  },
  messageCreatedAt: {
    fontSize: 10,
    marginHorizontal: 5,
    marginTop: 5,
  },
  buttonContentLeft: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    width: "80%",
  },
  isOnline: {
    fontSize: 13,
  },
  messageProps: {
    flexDirection: "row",
    alignItems: "center",
  },
});


export default MessagesContainer;
