import React, { useEffect, useState } from 'react';
import { Button, StatusBar, StyleSheet, View } from "react-native";
import { RootStackScreenProps } from "../../types";
import * as uuid from 'uuid';
import io from 'socket.io-client';
import { TextInput, Text } from '../../src/components/Themed';

const socketEndPoint = "http://localhost:3000";

export default function Chat({ navigation }: RootStackScreenProps<"Chat">) {
  const [socket, setSocket] = useState();
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [value, setValue] = useState("");
  const [name, setName] = useState("");

  useEffect(() => {
    const socketio = io(socketEndPoint, {
      transports: ["websocket"],
    });

    setSocket(socketio);
  }, []);

  useEffect(() => {
    if(!socket) return;
    socket.io.on("open", () => console.log("connected"));
    socket.io.on("close", () => console.log("desconnected"));

    function receivedMessage(message: Payload) {
          const newMessage: Message = {
            id: uuid.v4(),
            name: message.name,
            text: message.text,
          };

          setMessages([...messages, newMessage]);
        }

    socket.on("message", (message) => {
      receivedMessage(message);
    });
  }, [socket]);

  function sendMessage() {
    if(text.length) {
      const message = {
        name,
        text,
      };

      socket.emit("message", message);
      setText("");
    }
  }

  return (
    <View style={styles.container}>
      <TextInput value={name} onChangeText={(value) => setName(value)} placeholder="enter your name"/>
      <View style={styles.messageCard}>
      {messages.map((message, index) => {
            if (message.name === name) {
              return (
                <Text key={index}>
                  {message.name} {message.text}
                </Text>
              );
            }

            return (
              <Text key={index}>
                {message.name} {message.text}
              </Text>
            );
          })}
      </View>
      <TextInput value={text} onChangeText={(value) => setText(value)} placeholder='Send a message'/>
      <Button title='Send' onPress={() => sendMessage()}/>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  messageCard: {
    borderWidth: 1,
    borderColor: '#222',
  },
  myMessage: {
    backgroundColor: 'blue',
  },
  otherMessage: {
    backgroundColor: 'gray',
  },
});
