const crypto = require('crypto');

export function Cipher(text, key) {
  const cipher = crypto.createCipher('aes-128-cbc', key);
  const payloadString = cipher.update(text, 'utf8', 'hex');
  return payloadString;
}

export function Decipher(text, key) {
  const decipher = crypto.createDecipher('aes-128-cbc', key);
  const payloadString = decipher.update(text, 'hex', 'utf8');
  return payloadString;
}
