import React, { useEffect, useState, useContext, useRef } from "react";
import {
  Button,
  StatusBar,
  StyleSheet,
  View,
  Image,
  Pressable,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import {
  AntDesign,
  Ionicons,
  Feather,
  FontAwesome,
  MaterialIcons,
  Fontisto,
} from "@expo/vector-icons";
import { RootStackScreenProps } from "../../types";
import { TextInput, Text } from "../../src/components/Themed";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import Socket, { CreateSocketConnection } from "../../src/utils/socket";
import { getColors } from "../../constants/Colors";

export default function Chat({
  navigation,
  route,
}: RootStackScreenProps<"Chat">) {
  const [socket, setSocket] = useState();
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [chat, setChat] = useState();

  const { user } = useContext(UserContext);
  const { chats, updateChats } = useContext(ChatsContext);
  const colors = getColors();
  const scrollViewRef = useRef();

  useEffect(() => {
    const chatId = route.params.id;
    const socketConnection = CreateSocketConnection();
    socketConnection.emit("joinRoom", chatId);
    setSocket(socketConnection);

    if (chatId) {
      const thisChat = chats.filter((c) => c.id === chatId)[0];

      if (thisChat) {
        setChat(thisChat);
      } else {
        setChat(new Socket.sendHalkMessage());
      }

      socketConnection.on("receiveMessage", (msg, client) => {
        if (thisChat) {
          thisChat.messages.push(msg);
          updateChats({ chats });
        }

        if (!messages.length) {}

        setMessages((oldMessages) => [...oldMessages, msg]);
      });
    }
  }, [user, chats]);

  useEffect(() => {
    if (chat) {
      setMessages((oldMessages) => [...oldMessages, ...chat.messages]);
    }
  }, [chat]);

  function sendMessage() {
    const timeNow = new Date().getTime();
    const message = {
      author: user,
      content: text,
      createdAt: timeNow,
      id: `${timeNow}${Math.floor(
        Math.random() * (100000000 - 1000000 + 1) + 1000000
      )}`,
    };

    socket.emit("sendMessage", { room: chat.id, message });
    setText("");
  }

  return (
    <View style={styles.container}>
      {!chat ? (
        <></>
      ) : (
        <>
          <View
            style={[
              styles.topContainer,
              { backgroundColor: colors.defaultColors.card },
            ]}
          >
            <View style={styles.topContainerLeft}>
              <Pressable
                style={({ pressed }) => [
                  styles.backButton,
                  { opacity: pressed ? 0.5 : 1 },
                ]}
                onPress={() => navigation.navigate("Root")}
              >
                <AntDesign name="arrowleft" size={25} color={colors.tint} />
              </Pressable>

              <Pressable onPress={() => {}} style={styles.userProfile}>
                <Image
                  source={
                    chat.user.profile.avatar
                      ? { uri: chat.user.profile.avatar }
                      : require("../../assets/images/userIcon.png")
                  }
                  style={styles.userIcon}
                />
                <Text style={{ fontWeight: "bold" }}>
                  {chat.user.profile.name}
                </Text>
              </Pressable>
            </View>

            <View style={styles.topContainerRight}>
              <Pressable
                style={({ pressed }) => [
                  styles.backButton,
                  { opacity: pressed ? 0.5 : 1 },
                ]}
                onPress={() => {}}
              >
                <Ionicons name="call" size={24} color={colors.tint} />
              </Pressable>
              <Pressable
                style={({ pressed }) => [
                  styles.backButton,
                  { opacity: pressed ? 0.5 : 1 },
                ]}
                onPress={() => {}}
              >
                <FontAwesome
                  name="video-camera"
                  size={24}
                  color={colors.tint}
                />
              </Pressable>
              <Pressable
                style={({ pressed }) => [
                  styles.backButton,
                  { opacity: pressed ? 0.5 : 1 },
                ]}
                onPress={() => {}}
              >
                <Feather name="more-vertical" size={24} color={colors.tint} />
              </Pressable>
            </View>
          </View>

          <ScrollView
            style={styles.messageContainer}
            ref={scrollViewRef}
            onContentSizeChange={() =>
              scrollViewRef.current.scrollToEnd({ animated: true })
            }
          >
            {messages.map((message, index) => {
              const itsMyMessage = message.author.id === user.id ? true : false;
              const messageTime = new Date(
                message.createdAt
              ).toLocaleTimeString();

              return (
                <View
                  style={itsMyMessage ? styles.myMessage : styles.otherMessage}
                  key={index}
                >
                  <Pressable
                    style={[
                      itsMyMessage
                        ? { backgroundColor: "#2f95dc", maxWidth: "85%" }
                        : { backgroundColor: colors.defaultColors.card },
                      styles.message,
                    ]}
                    onLongPress={() => alert(JSON.stringify(message))}
                  >
                    <Text
                      style={[
                        { maxWidth: "85%" },
                        {
                          color: itsMyMessage
                            ? "white"
                            : colors.defaultColors.text,
                        },
                      ]}
                    >
                      {message.content}
                    </Text>
                    <Text
                      style={[
                        styles.messageCreatedAt,
                        {
                          color: itsMyMessage
                            ? "white"
                            : colors.defaultColors.text,
                        },
                      ]}
                    >
                      {messageTime.slice(0, 5)}
                    </Text>
                  </Pressable>
                </View>
              );
            })}
          </ScrollView>

          <View
            style={[
              styles.bottomContainer,
              { backgroundColor: colors.defaultColors.card },
            ]}
          >
            <View style={styles.buttonContentLeft}>
              <TouchableOpacity style={styles.blueButton} onPress={() => {}}>
                <Fontisto name="camera" size={20} color="white" />
              </TouchableOpacity>
              <TextInput
                style={styles.inputMessage}
                placeholder="Mensagem..."
                value={text}
                onChangeText={(value) => setText(value)}
              />
            </View>

            {text ? (
              <TouchableOpacity
                style={styles.blueButton}
                onPress={() => sendMessage()}
              >
                <FontAwesome name="send" size={24} color="white" />
              </TouchableOpacity>
            ) : (
              <View style={styles.othersMedias}>
                <TouchableOpacity onPress={() => {}}>
                  <Ionicons
                    name="ios-add-circle-outline"
                    size={26}
                    color={colors.tint}
                  />
                </TouchableOpacity>
                <TouchableOpacity style={{ marginLeft: 8 }} onPress={() => {}}>
                  <MaterialIcons
                    name="keyboard-voice"
                    size={26}
                    color={colors.tint}
                  />
                </TouchableOpacity>
              </View>
            )}
          </View>
        </>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  topContainer: {
    flexDirection: "row",
    alignItems: "center",
    padding: 5,
    justifyContent: "space-between",
  },
  topContainerLeft: {
    flexDirection: "row",
    alignItems: "center",
  },
  topContainerRight: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  messageContainer: {
    flex: 1,
    marginHorizontal: 10,
    flexDirection: "column-reverse",
    marginBottom: 55,
  },
  bottomContainer: {
    flexDirection: "row",
    alignItems: "center",
    position: "absolute",
    backgroundColor: "rgb(18, 18, 18)",
    justifyContent: "space-between",
    borderRadius: 50,
    bottom: 0,
    left: 0,
    right: 0,
    padding: 5,
    margin: 5,
  },
  messageCard: {
    borderWidth: 1,
    borderColor: "#222",
  },
  myMessage: {
    justifyContent: "flex-end",
    flexDirection: "revert",
  },
  otherMessage: {
    width: "fit-content",
    maxWidth: "85%",
  },
  userIcon: {
    width: 40,
    height: 40,
    borderRadius: 100,
    marginHorizontal: 5,
  },
  userProfile: {
    flexDirection: "row",
    alignItems: "center",
  },
  backButton: {
    padding: 5,
    borderRadius: 100,
  },
  blueButton: {
    backgroundColor: "#2f95dc",
    padding: 8,
    borderRadius: 100,
  },
  othersMedias: {
    flexDirection: "row",
    alignItems: "center",
  },
  inputMessage: {
    paddingVertical: 8,
    width: "100%",
    marginLeft: 10,
  },
  message: {
    padding: 7,
    borderRadius: 20,
    flexDirection: "row",
    marginBottom: 10,
    alignItems: "flex-end",
    flexWrap: "wrap",
  },
  messageCreatedAt: {
    fontSize: 10,
    marginLeft: 5,
  },
  buttonContentLeft: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
});
