import { createContext, useEffect, useState } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";

interface SocketInterface {
  logged: boolean;
  socket: Object;
  updateSocket: Function;
}

export const SocketContext = createContext<SocketInterface>({} as SocketInterface);

export const SocketProvider = ({ children }: any) => {
  const [loads, setLoads] = useState(0);

  const updateSocket = (newSocket:any) => {
    console.log(newSocket);

    setSocket({
      socket: newSocket.socket,
      updateSocket: updateSocket,
    });
  };

  const initialValue: SocketInterface = {
    socket: {},
    updateSocket: updateSocket,
  };

  const [socket, setSocket] = useState<SocketInterface>(initialValue);

  useEffect(() => {
    AsyncStorage.getItem("socket").then((data: any) => {
      const dt = JSON.parse(data);

      if (dt) {
        updateSocket(dt);
      } else updateSocket(initialValue);
    });
  }, []);

  useEffect(() => {
    return console.log(socket);

    if (socket !== initialValue) {
      AsyncStorage.setItem("socket", JSON.stringify(socket));
    }

    setLoads(loads + 1);
  }, [socket]);

  if (loads === 0) return;

  return <SocketContext.Provider value={socket}>{children}</SocketContext.Provider>;
};
