import React, { useContext, useEffect } from "react";
import { Pressable, View, StyleSheet, ScrollView, Image } from "react-native";
import { RootTabScreenProps } from "../../types";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import { Text } from "../../src/components/Themed";
import { Ionicons } from "@expo/vector-icons";
import NewChatButton from "../../src/components/newChat";
import { getColors } from "../../constants/Colors";
import Socket, { CreateSocketConnection } from "../../src/utils/socket";
import { Cipher, Decipher } from "../../middleware/crypto";
import { Userpic } from "react-native-userpic";

const badgeProps = {
  size: 30,
  borderRadius: 5,
};

export default function Chats({ navigation }: RootTabScreenProps<"Chats">) {
  const { user, logged, updateUser } = useContext(UserContext);
  const { chats, updateChats } = useContext(ChatsContext);
  const colors = getColors();

  useEffect(() => {
    const socketConnection = CreateSocketConnection({ userId: user.id });
    socketConnection.on("newChat", ({ newChat }) => {
      if (newChat) {
        chats.push(newChat);
        updateChats({ chats });
      }
    });
    socketConnection.on("receiveMessage", (msg) => {
      if (msg) {
        const thisChat = chats.filter((c) => c.user.id === msg.author.id)[0];
        if (thisChat) {
          if (thisChat.messages.some((m) => m.id === msg.id)) return;
          thisChat.messages.push(msg);
          updateChats({ chats });
        }
      }
    });
  }, []);

  function ChatContainer({ chat }) {
    if (!chat.id) return;
    const lastMessage = chat.messages[chat.messages.length - 1];
    if (!lastMessage) return null;
    const unredMessages = chat.messages.filter((m) => !m.read);
    const messageTime = new Date(lastMessage.createdAt).toLocaleTimeString();
    let content = chat.key
      ? Decipher(lastMessage.content, chat.key)
      : "Nova mensagem";

    return (
      <Pressable
        style={({ pressed }) => [
          styles.chat,
          {
            backgroundColor: pressed
              ? colors.defaultColors.card
              : "transparent",
          },
        ]}
        onPress={() =>
          navigation.navigate("Chat", {
            id: chat.id,
          })
        }
      >
        <View style={styles.leftContent}>
          <Userpic
            size={75}
            name={chat.user.profile.name}
            colorize={true}
            borderRadius="100%"
            badge={35}
            badgeColor="#007aff"
            badgeProps={badgeProps}
          />
          <View style={{ justifyContent: "center" }}>
            <Text style={styles.username}>{chat.user.profile.name}</Text>
            {lastMessage.author.id === user.id ? (
              <View style={{ flexDirection: "row", alignItems: "flex-end" }}>
                <Ionicons
                  name="checkmark-done"
                  size={20}
                  color="gray"
                  style={{ marginRight: 2 }}
                />
                <Text style={styles.messageContent}>
                  {content.length > 28 ? content.slice(0, 28) + "..." : content}
                </Text>
              </View>
            ) : (
              <Text>
                {content.length > 30 ? content.slice(0, 30) + "..." : content}
              </Text>
            )}
          </View>
        </View>
        <View style={styles.rightContent}>
          <Text style={styles.messageDate}>{messageTime.slice(0, 5)}</Text>
          {!unredMessages.length ? (
            <></>
          ) : (
            <Text style={styles.unread}>{unredMessages.length}</Text>
          )}
        </View>
      </Pressable>
    );
  }

  return (
    <View style={styles.container}>
      {!logged ? (
        <>{navigation.navigate("Welcome")}</>
      ) : (
        <ScrollView style={{ padding: 5 }}>
          {!chats.length ? (
            <ChatContainer chat={new Socket.sendHalkMessage()} />
          ) : (
            <>
              {chats.map((chat, index) => (
                <ChatContainer chat={chat} key={index} />
              ))}
            </>
          )}
        </ScrollView>
      )}
      <NewChatButton navigation={navigation} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  chat: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: 10,
    padding: 5,
  },
  leftContent: {
    flexDirection: "row",
  },
  userIcon: {
    width: 50,
    height: 50,
    borderRadius: 100,
    marginRight: 10,
  },
  username: {
    fontWeight: "bold",
  },
  messageContent: {
    fontSize: 13,
    opacity: 0.7,
    alignItems: "center",
  },
  rightContent: {
    alignItems: "center",
  },
  messageDate: {
    fontSize: 11,
    marginBottom: 3,
  },
  unread: {
    backgroundColor: "#2f95dc",
    borderRadius: 100,
    minWidth: 15,
    minHeight: 15,
    textAlign: "center",
    color: "white",
  },
});
