import { useEffect, useState, useContext } from "react";
import { View, StyleSheet, StatusBar, ScrollView } from "react-native";
import { RootTabScreenProps } from "../../types";
import { Text, TextInput } from "../../src/components/Themed";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import { Userpic } from "react-native-userpic";
import { FontAwesome, Entypo, EvilIcons } from "@expo/vector-icons";
import { getColors } from "../../constants/Colors";

const badgeProps = {
  size: 35,
  borderRadius: 50,
  animate: true,
};

export default function Status({ navigation }: RootTabScreenProps<"Status">) {
  const { user } = useContext(UserContext);
  const { chats } = useContext(ChatsContext);

  const colors = getColors();

  console.log(colors);

  return (
    <View style={styles.container}>
      <View style={styles.userContainer}>
        <View style={styles.rowContainer}>
          <Userpic
            size={50}
            name={user.profile.name}
            source={{ uri: user.profile.avatar }}
            colorize={true}
            borderRadius="50%"
            badge={true}
            badgeColor={"#2f95dc"}
            badgePosition={"bottom-right"}
            badgeProps={badgeProps}
            style={{ marginRight: 10 }}
          />
          <View>
            <Text style={{ fontWeight: "bold" }}>Meu status</Text>
            <Text style={{ fontSize: 13 }}>
              Toque para atualizar seu status
            </Text>
          </View>
        </View>
        <View style={styles.rowContainer}>
          <View style={styles.blueButton}>
            <Entypo name="camera" size={22} color="white" />
          </View>
          <View style={styles.blueButton}>
            <FontAwesome name="pencil" size={22} color="white" />
          </View>
        </View>
      </View>

      <View style={[styles.searchContainer, { borderBottomColor: colors.defaultColors.border }]}>
        <TextInput placeholder="Pesquisar" style={[styles.searchInput, { backgroundColor: colors.defaultColors.card }]}/>
      </View>
      <ScrollView style={styles.statusContainer}></ScrollView>

      <View style={styles.searchSection}>
    <EvilIcons name="search" size={24} color={colors.tint} />
    <TextInput
        style={styles.input}
        placeholder="User Nickname"
        onChangeText={(searchString) => {this.setState({searchString})}}
        underlineColorAndroid="transparent"
    />
</View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
    margin: 10,
  },
  userContainer: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  statusContainer: {},
  rowContainer: {
    flexDirection: "row",
    alignItems: "center",
  },
  blueButton: {
    ṕadding: 10,
    borderRadius: 100,
    backgroundColor: "#2f95dc",
    width: 35,
    height: 35,
    alignItems: "center",
    justifyContent: "center",
    marginRight: 5,
  },
  searchContainer: {
    borderBottomWidth: 1,
    padding: 10,
  },
  searchInput: {
    borderRadius: 10,
    width: '100%',
    height: 30,
    padding: 10,
  },
  searchSection: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
},
searchIcon: {
    padding: 10,
},
input: {
    flex: 1,
    paddingTop: 10,
    paddingRight: 10,
    paddingBottom: 10,
    paddingLeft: 0,
    backgroundColor: '#fff',
    color: '#424242',
},
});
