import React, { useContext, useEffect } from "react";
import { Pressable, View, StyleSheet, ScrollView, Image } from "react-native";
import { RootTabScreenProps } from "../../types";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import { Text } from "../../src/components/Themed";
import { Ionicons } from "@expo/vector-icons";

export default function Chats({ navigation }: RootTabScreenProps<"Chats">) {
  const { user, logged, updateUser } = useContext(UserContext);
  const { chats } = useContext(ChatsContext);

  useEffect(() => {}, [chats, user]);

  return (
    <View style={styles.container}>
      {!logged ? (
        <>{navigation.navigate("Welcome")}</>
      ) : (
        <ScrollView style={{ padding: 5 }}>
          {!chats.length ? (
            <Pressable
              style={({ pressed }) => [
                styles.chat,
                { backgroundColor: pressed ? "#222" : "transparent" },
              ]}
              onPress={() => navigation.navigate("Chat", {
                params: {
                  id: '0',
                }
              })}
            >
              <View style={styles.leftContent}>
                <Image
                  source={require("../../assets/images/userIcon.png")}
                  style={styles.userIcon}
                />
                <View style={{ justifyContent: "center" }}>
                  <Text style={styles.username}>Halk</Text>
                  <Text style={styles.messageContent}>Welcome to halk!</Text>
                </View>
              </View>
              <View style={styles.rightContent}>
                <Text style={styles.messageDate}>
                  {new Date().getHours()}:{new Date().getMinutes()}
                </Text>
                <Text style={styles.unread}>1</Text>
              </View>
            </Pressable>
          ) : (
            <>
              {chats.map((chat, index) => {
                const lastMessage = chat.messages[chat.messages.length - 1];
                const unredMessages = chat.messages.filter((m) => m.unred);

                return (
                  <Pressable
                    style={({ pressed }) => [
                      styles.chat,
                      { backgroundColor: pressed ? "#222" : "transparent" },
                    ]}
                    key={index}
                  >
                    <View style={styles.leftContent}>
                      <Image
                        source={{ uri: chat.user.profile.avatar }}
                        style={styles.userIcon}
                      />
                      <View style={{ justifyContent: "center" }}>
                        <Text style={styles.username}>
                          {chat.user.profile.name}
                        </Text>
                        <Text style={styles.messageContent}>
                          {lastMessage.user.id === user.id ? (
                            <Ionicons
                              name="checkmark-done"
                              size={24}
                              color="#222"
                            />
                          ) : (
                            <></>
                          )}
                          {lastMessage.content}
                        </Text>
                      </View>
                    </View>
                    <View style={styles.rightContent}>
                      <Text style={styles.messageDate}>
                        {lastMessage.createdAt}
                      </Text>
                      {!unredMessages.length ? (
                        <></>
                      ) : (
                        <Text style={styles.unread}>
                          {unredMessages.length}
                        </Text>
                      )}
                    </View>
                  </Pressable>
                );
              })}
            </>
          )}
        </ScrollView>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  chat: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: 10,
    padding: 5,
  },
  leftContent: {
    flexDirection: "row",
  },
  userIcon: {
    width: 50,
    height: 50,
    borderRadius: 100,
    marginRight: 10,
  },
  username: {},
  messageContent: {
    fontSize: 13,
    opacity: 0.7,
  },
  rightContent: {
    alignItems: "center",
  },
  messageDate: {
    fontSize: 11,
    marginBottom: 3,
  },
  unread: {
    backgroundColor: "#2f95dc",
    borderRadius: 100,
    minWidth: 15,
    minHeight: 15,
    textAlign: "center",
  },
});
