import { StatusBar } from "expo-status-bar";
import { SafeAreaProvider } from "react-native-safe-area-context";
import useCachedResources from "./src/hooks/useCachedResources";
import useColorScheme from "./src/hooks/useColorScheme";
import Navigation from "./navigation";
import { UserProvider } from "./src/contexts/user";
import { ChatsProvider } from "./src/contexts/chats";
import { SocketContext, SocketProvider } from "./src/contexts/socket";
import ErrorBoundary from "./screens/errorBoundary";
import { CreateSocketConnection } from '../src/utils/socket';

export default function App() {
  const isLoadingComplete = useCachedResources();
  const colorScheme = useColorScheme();

  if (!isLoadingComplete) {
    return null;
  } else {
    return (
      <SocketContext.Provider value={{socket: CreateSocketConnection({ userId: "6ee19c72-e5fc-4da6-8f2a-a8b397f46f4d" })}}>
        <UserProvider>
          <ChatsProvider>
            <SafeAreaProvider>
              <ErrorBoundary>
                <Navigation colorScheme={colorScheme} />
                <StatusBar />
              </ErrorBoundary>
            </SafeAreaProvider>
          </ChatsProvider>
        </UserProvider>
      </SocketContext.Provider>
    );
  }
}
