import { useState } from "react";
import {
  Text,
  View,
  Image,
  Pressable,
  StyleSheet,
  StatusBar,
} from "react-native";
import { RootStackScreenProps } from "../../types";
import Translate from "../../middleware/translate";

export default function Welcome({
  navigation,
}: RootStackScreenProps<"Welcome">) {
  const [page, setPage] = useState(0);

  const pages = [
    {
      title: "Welcome to Halk!",
      description:
        "This is a basic and open source template of a real-time messaging app made in react-native and expo, visit the source code on our github, download and customize it however you want!",
      image: require("../../assets/images/favicon.png"),
    },
  ];

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{pages[page].title}</Text>
      <Image source={pages[page].image} style={styles.image} />
      <Text style={styles.description}>{pages[page].description}</Text>
      <Pressable
        style={styles.button}
        onPress={() => {
          if (pages[page + 1]) {
            setPage(page + 1);
          } else navigation.navigate("Register");
        }}
      >
        <Text>Continue</Text>
      </Pressable>
      <View style={styles.ballsContainer}>
        {pages.map((t, index) => (
          <Text style={styles.ball} key={index}>
            {" "}
          </Text>
        ))}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
    alignItems: "center",
  },
  title: {
    fontWeight: "bold",
    marginBottom: 20,
    textAlign: 'center',
  },
  image: {
    width: 250,
    height: 250,
  },
  description: {
    fontSize: 12,
    marginTop: 20,
    marginBottom: 20,
  },
  button: {
    backgroundColor: "#2f95dc",
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 50,
  },
  ballsContainer: {
    alignItems: "center",
    justifyContent: "center",
    position: 'absolute',
    bottom: 5,
  },
  ball: {
    width: 10,
    height: 10,
    backgroundColor: "#2f95dc",
    borderRadius: 100,
  },
});
