import React, { useEffect, useState, useContext } from "react";
import { Button, StatusBar, StyleSheet, View } from "react-native";
import { AntDesign, Ionicons, Feather, FontAwesome } from "@expo/vector-icons";
import { RootStackScreenProps } from "../../types";
import { TextInput, Text } from "../../src/components/Themed";
import Socket from "../../src/utils/socket";
import { UserContext } from "../../src/contexts/user";

export default function Chat({ navigation, route }: RootStackScreenProps<"Chat">) {
  const [socket, setSocket] = useState();
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [value, setValue] = useState("");
  const [name, setName] = useState("");
  const [ready, setReady] = useState(false);
  const [otherUser, setOtherUser] = useState();

  const { user } = useContext(UserContext);

  useEffect(() => {
    if (user.id) {
      new Socket().get().then((socket) => {
        socket.emit("getMessages", (msgs) => {
          if (msgs.length) {
            setMessages(msgs);
            setOtherUser(msgs[0].author);
          } else {
            setMessages([
              {
                text: "Welcome to halk!",
                author: {
                  id: "0",
                },
              },
            ]);

            setOtherUser({
              id: "0",
              profile: {},
            });
          }
        });
      });
    }

    console.log(navigation, route);
  }, [user]);

  return (
    <View style={styles.container}>
      <View style={styles.topContainer}></View>

      <View style={styles.messageContainer}></View>

      <View style={styles.bottomBar}></View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  topContainer: {},
  leftContent: {},
  rightContent: {},
  otherUserInfo: {},
  otherUserName: {},
  messageContainer: {},
  bottomBar: {},
  messageCard: {
    borderWidth: 1,
    borderColor: "#222",
  },
  myMessage: {
    backgroundColor: "blue",
  },
  otherMessage: {
    backgroundColor: "gray",
  },
});
