 import crypto from 'crypto-js';

 export function Cipher(stc, key) {
    const cryptoInfo = crypto.AES.encrypt(JSON.stringify({ str }), key).toString();

    console.log({ cryptoInfo });
    const info2 = crypto.AES.decrypt(cryptoInfo, key).toString(crypto.enc.Utf8);

    console.log({ info2 });

    const info3 = JSON.parse(info2);

    console.log({ str: info3.str });
 }

export function Decipher(str, key) {
  const cryptoInfo = crypto.AES.encrypt(JSON.stringify({ str }), key).toString();

    console.log(cryptoInfo);


    const info2 = crypto.AES.decrypt(cryptoInfo, key).toString(crypto.enc.Utf8);

    console.log({ info2 });
}
