import { useState } from "react";
import { Image, Pressable, StatusBar, StyleSheet, View } from "react-native";
import { RootStackScreenProps } from "../../types";
import { Text, TextInput } from "../../src/components/Themed";
import * as ImagePicker from "expo-image-picker";

export default function CreateProfile({
  navigation,
}: RootStackScreenProps<"CreateProfile">) {
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [icon, setIcon] = useState("");
  const [bio, setBio] = useState("");

  function Create() {
    console.log({
      name,
      username,
      icon,
      bio,
    });
    navigation.navigate('Chats');
  }

  async function SelectIcon() {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!result.cancelled) {
      setIcon(result.uri);
    }
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Create your profile</Text>
      <View style={styles.primaryDiv}>
        <Pressable style={styles.iconContainer} onPress={() => SelectIcon()}>
          <Image
            source={icon ? icon : require("../../assets/images/userIcon.png")}
            style={styles.userIcon}
          />
          <Text style={{ fontSize: 10 }}>Choose a icon</Text>
        </Pressable>
        <View style={styles.inputsContainer}>
          <View style={styles.inputContainer}>
            <Text style={styles.inputTitle}>Name</Text>
            <TextInput
              style={styles.nameInput}
              onChangeText={(value) => setName(value)}
            />
          </View>
          <View style={styles.inputContainer}>
            <Text style={styles.inputTitle}>Username</Text>
            <TextInput
              style={styles.nameInput}
              onChangeText={(value) => setUsername(value)}
            />
          </View>
        </View>
      </View>
      <View style={styles.secondaryDiv}>
        <Text style={styles.inputTitle}>Bio</Text>
        <TextInput
          style={styles.bioInput}
          onChangeText={(value) => setBio(value)}
        />
      </View>
      <Pressable style={styles.nextButton} onPress={() => Create()}>
        <Text style={{ color: "white" }}>Continue</Text>
      </Pressable>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  title: {
    fontWeight: "bold",
    textAlign: "center",
    margin: 10,
  },
  primaryDiv: {
    flexDirection: "row",
    alignItems: "center",
  },
  secondaryDiv: {
    margin: 10,
  },
  userIcon: {
    width: 100,
    height: 100,
    marginBottom: 5,
    borderRadius: 100,
  },
  iconContainer: {
    margin: 10,
    alignItems: "center",
  },
  inputsContainer: {},
  inputContainer: {},
  inputTitle: {
    fontSize: 10,
  },
  nameInput: {
    borderBottomColor: "#222",
    borderBottomWidth: 1,
    paddingVertical: 3,
    marginBottom: 10,
  },
  bioInput: {
    borderWidth: 1,
    borderColor: "#222",
    borderRadius: 10,
    marginTop: 5,
    height: 70,
  },
  nextButton: {
    backgroundColor: "#2f95dc",
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 50,
    position: "absolute",
    bottom: 10,
    left: "40%",
  },
});
