import React, { Component, ReactElement, useState, useContext } from "react";
import { StyleSheet, TouchableOpacity, View } from "react-native";
import { Text } from "../Themed";
import { Userpic } from "react-native-userpic";
import CreateSocketConnectio from "../../utils/socket";
import { UserContext } from "../../contexts/user";
import { ChatsContext } from "../../contexts/chats";
import { Cipher, Decipher } from "../../../middleware/crypto";
import { Ionicons } from "@expo/vector-icons";

class ChatContainer extends Component {
  badgeProps = {
    size: 25,
    borderRadius: 50,
    animate: true,
  };

  constructor(props) {
    super(props);
    this.states = {
      isOnline: false,
    };

    this.chat = props.chat;
    this.navigation = props.navigation;
    this.socket = props.socket;
    this.user = props.user;
    this.chats = props.chats;
    this.updateChats = props.updateChats;
  }

  EditChat(): ReactElement<any> {
    return <></>;
  }

  render(): ReactElement<any> {
    const lastMessage = this.chat.messages[this.chat.messages.length - 1];
    if (!lastMessage) return null;
    const unredMessages = this.chat.messages.filter((m) => m.read === false);
    const messageTime = new Date(lastMessage.createdAt).toLocaleTimeString();

    if (this.socket) {
      this.socket.emit("verifyIfUserIsOnline", { userId: this.chat.user.id });
      this.socket.on(
        "receiveIfUserIsOnline",
        (callback) => (this.states.isOnline = callback)
      );
    }

    let content = this.chat.key
      ? Decipher(lastMessage.content, this.chat.key)
      : "Nova mensagem";

    return (
      <TouchableOpacity
        style={styles.chatContainer}
        onPress={() => {
          this.navigation.navigate("Chat", {
            id: this.chat.id,
          })
        }}
        onLongPress={() => {}}
      >
        <this.EditChat />
        <View style={{ flexDirection: "row" }}>
          <Userpic
            size={40}
            name={this.chat.user.profile.name}
            source={{ uri: this.chat.user.profile.avatar }}
            colorize={true}
            borderRadius="50%"
            badge={true}
            badgeColor={this.states.isOnline ? "#00ff0d" : "#919191"}
            badgePosition={"bottom-right"}
            badgeProps={this.badgeProps}
            style={{ marginRight: 10 }}
          />
          <View style={{ justifyContent: "center" }}>
            <Text style={{ fontWeight: "bold" }}>
              {this.chat.user.profile.name}
            </Text>
            {lastMessage.author.id === this.user.id ? (
              <View style={{ flexDirection: "row", alignItems: "center" }}>
                <Ionicons
                  name="checkmark-done"
                  size={20}
                  color={lastMessage.read ? "#2f95dc" : "gray"}
                  style={{ marginRight: 2 }}
                />
                <Text style={styles.messageContent}>
                  {content.length > 28 ? content.slice(0, 28) + "..." : content}
                </Text>
              </View>
            ) : (
              <Text>
                {content.length > 30 ? content.slice(0, 30) + "..." : content}
              </Text>
            )}
          </View>
        </View>
        <View style={{ alignItems: "center" }}>
          <Text style={styles.messageDate}>{messageTime.slice(0, 5)}</Text>
          {!unredMessages.length ? (
            <></>
          ) : (
            <Text style={styles.unread}>{unredMessages.length}</Text>
          )}
        </View>
      </TouchableOpacity>
    );
  }
}

const styles = StyleSheet.create({
  chatContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: 10,
    padding: 5,
  },
  messageContent: {
    fontSize: 13,
    opacity: 0.7,
    alignItems: "center",
  },
  messageDate: {
    fontSize: 11,
    marginBottom: 3,
  },
  unread: {
    backgroundColor: "#2f95dc",
    borderRadius: 100,
    minWidth: 15,
    minHeight: 15,
    textAlign: "center",
    color: "white",
  },
});

export default ChatContainer;
