import React, { useEffect, useState, useContext, useRef } from "react";
import {
  Button,
  StatusBar,
  StyleSheet,
  View,
  Image,
  Pressable,
  ScrollView,
} from "react-native";
import {
  AntDesign,
  Ionicons,
  Feather,
  FontAwesome,
  MaterialIcons,
  Fontisto,
} from "@expo/vector-icons";
import { RootStackScreenProps } from "../../types";
import { TextInput, Text } from "../../src/components/Themed";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import Socket from "../../src/utils/socket";
import { getColors } from "../../constants/Colors";

export default function Chat({
  navigation,
  route,
}: RootStackScreenProps<"Chat">) {
  const [chat, setChat] = useState();
  const [socket, setSocket] = useState();
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState("");
  const [value, setValue] = useState("");
  const [name, setName] = useState("");
  const [ready, setReady] = useState(false);
  const [otherUser, setOtherUser] = useState();

  const scrollViewRef = useRef();

  const { user } = useContext(UserContext);
  const { chats } = useContext(ChatsContext);

  const colors = getColors();

  useEffect(() => {
    const chatId = route.params.id;

    if (chatId) {
      if (chatId === "0" || !chats.length) {
        setChat(new Socket.sendHalkMessage());
      } else {
        const thisChat = chats.filter((c) => c.id === chatId);
        if (thisChat) {
          setChat(thisChat);
        } else navigation.navigate("Root");
      }
    } else navigation.navigate("Root");
  }, [user]);

  useEffect(() => {}, [chat]);

  return (
    <View style={styles.container}>
      {!chat ? (
        <></>
      ) : (
        <>
          <View
            style={[
              styles.topContainer,
              { backgroundColor: colors.defaultColors.card },
            ]}
          >
            <View style={styles.topContainerLeft}>
              <Pressable
                style={({ pressed }) => [
                  styles.backButton,
                  { opacity: pressed ? 0.5 : 1 },
                ]}
                onPress={() => navigation.navigate("Root")}
              >
                <AntDesign name="arrowleft" size={25} color={colors.tint} />
              </Pressable>

              <Pressable onPress={() => {}} style={styles.userProfile}>
                <Image
                  source={
                    chat.user.profile.avatar
                      ? { uri: chat.user.profile.avatar }
                      : require("../../assets/images/userIcon.png")
                  }
                  style={styles.userIcon}
                />
                <Text style={{ fontWeight: "bold" }}>
                  {chat.user.profile.name}
                </Text>
              </Pressable>
            </View>

            <View style={styles.topContainerRight}>
              <Pressable
                style={({ pressed }) => [
                  styles.backButton,
                  { opacity: pressed ? 0.5 : 1 },
                ]}
                onPress={() => {}}
              >
                <Ionicons name="call" size={24} color={colors.tint} />
              </Pressable>
              <Pressable
                style={({ pressed }) => [
                  styles.backButton,
                  { opacity: pressed ? 0.5 : 1 },
                ]}
                onPress={() => {}}
              >
                <FontAwesome
                  name="video-camera"
                  size={24}
                  color={colors.tint}
                />
              </Pressable>
              <Pressable
                style={({ pressed }) => [
                  styles.backButton,
                  { opacity: pressed ? 0.5 : 1 },
                ]}
                onPress={() => {}}
              >
                <Feather name="more-vertical" size={24} color={colors.tint} />
              </Pressable>
            </View>
          </View>

          <ScrollView
            style={styles.messageContainer}
            ref={scrollViewRef}
            onContentSizeChange={() =>
              scrollViewRef.current.scrollToEnd({ animated: true })
            }
          >
            {chat.messages.map((message, index) => {
              const itsMyMessage = message.author.id === user.id ? true : false;

              return (
                <View
                  style={itsMyMessage ? styles.myMessage : styles.otherMessage}
                  key={index}
                >
                  <Pressable
                    style={[
                      itsMyMessage
                        ? { backgroundColor: "#2f95dc", maxWidth: "85%" }
                        : { backgroundColor: colors.defaultColors.card },
                      styles.message,
                    ]}
                    onLongPress={() => alert(JSON.stringify(message))}
                  >
                    <Text style={{ maxWidth: "85%" }}>{message.content}</Text>
                    <Text style={styles.messageCreatedAt}>
                      {message.createdAt}
                    </Text>
                  </Pressable>
                </View>
              );
            })}
          </ScrollView>

          <View
            style={[
              styles.bottomContainer,
              { backgroundColor: colors.defaultColors.card },
            ]}
          >
            <View style={{ flexDirection: "row", alignItems: "center" }}>
              <Pressable
                style={({ pressed }) => [
                  styles.openCamera,
                  { opacity: pressed ? 0.5 : 1 },
                ]}
                onPress={() => {}}
              >
                <Fontisto name="camera" size={18} color="white" />
              </Pressable>
              <TextInput
                style={styles.inputMessage}
                placeholder="Mensagem..."
                value={text}
                onChangeText={(value) => setText(value)}
              />
            </View>

            {text ? (
              <TouchableOpacity style={styles.openCamera} onPress={() => {}}>
                <FontAwesome name="send" size={24} color="white" />
              </TouchableOpacity>
            ) : (
              <View style={styles.othersMedias}>
                <Pressable
                  style={({ pressed }) => [
                    { marginRight: 5 },
                    { opacity: pressed ? 0.5 : 1 },
                  ]}
                  onPress={() => {}}
                >
                  <MaterialIcons
                    name="keyboard-voice"
                    size={24}
                    color={colors.tint}
                  />
                </Pressable>
                <Pressable
                  style={({ pressed }) => [{ opacity: pressed ? 0.5 : 1 }]}
                  onPress={() => {}}
                >
                  <Ionicons
                    name="ios-add-circle-outline"
                    size={24}
                    color={colors.tint}
                  />
                </Pressable>
              </View>
            )}
          </View>
        </>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  topContainer: {
    flexDirection: "row",
    alignItems: "center",
    padding: 5,
    justifyContent: "space-between",
  },
  topContainerLeft: {
    flexDirection: "row",
    alignItems: "center",
  },
  topContainerRight: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  otherUserInfo: {},
  otherUserName: {},
  messageContainer: {
    flex: 1,
    marginHorizontal: 10,
    flexDirection: "column-reverse",
    marginBottom: 50,
  },
  bottomContainer: {
    flexDirection: "row",
    alignItems: "center",
    position: "absolute",
    backgroundColor: "rgb(18, 18, 18)",
    justifyContent: "space-between",
    borderRadius: 50,
    bottom: 0,
    left: 0,
    right: 0,
    padding: 5,
    margin: 5,
  },
  messageCard: {
    borderWidth: 1,
    borderColor: "#222",
  },
  myMessage: {
    justifyContent: "flex-end",
    flexDirection: "revert",
  },
  otherMessage: {
    width: "fit-content",
    maxWidth: "85%",
  },
  userIcon: {
    width: 40,
    height: 40,
    borderRadius: 100,
    marginHorizontal: 5,
  },
  userProfile: {
    flexDirection: "row",
    alignItems: "center",
  },
  backButton: {
    padding: 5,
    borderRadius: 100,
  },
  openCamera: {
    backgroundColor: "#2f95dc",
    padding: 5,
    borderRadius: 100,
    marginRight: 10,
  },
  othersMedias: {
    flexDirection: "row",
    alignItems: "center",
  },
  inputMessage: {},
  message: {
    padding: 7,
    borderRadius: 20,
    flexDirection: "row",
    marginBottom: 10,
    alignItems: "flex-end",
    flexWrap: "wrap",
  },
  messageCreatedAt: {
    fontSize: 10,
    marginLeft: 5,
  },
});
