import React, { useContext, useEffect } from "react";
import { Pressable, View, StyleSheet, ScrollView, Image } from "react-native";
import { RootTabScreenProps } from "../../types";
import { UserContext } from "../../src/contexts/user";
import { ChatsContext } from "../../src/contexts/chats";
import { Text } from "../../src/components/Themed";

export default function Chats({ navigation }: RootTabScreenProps<"Chats">) {
  const { user, logged, updateUser } = useContext(UserContext);
  const { chats } = useContext(ChatsContext);

  useEffect(() => {}, [chats, user]);

  return (
    <View style={styles.container}>
      {!logged ? (
        <>{navigation.navigate("Welcome")}</>
      ) : (
        <ScrollView style={{paddingHorizontal: 10}}>
          {!chats.length ? (
            <Pressable style={({ pressed }) => ([styles.chat, {opacity: pressed ? 0.5 : 1}])}>
              <View style={styles.leftContent}>
              <Image source={require('../../assets/images/icon.png')} style={styles.userIcon}/>
                <View style={{justifyContent: 'center'}}>
                <Text style={styles.username}>Halk</Text>
                <Text style={styles.messageContent}>Welcome to halk!</Text>
                </View>
              </View>
              <View style={styles.rightContent}>
              <Text style={styles.messageDate}>{new Date().getHours()}:{new Date().getMinutes()}</Text>
              <Text style={styles.unread}>1</Text>
              </View>
            </Pressable>
          ) : (
            <>
              {chats.map((chat, index) => (
                <Pressable style={styles.chat} key={index}></Pressable>
              ))}
            </>
          )}
        </ScrollView>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  chat: {
    marginTop: 5,
    flexDirection: "row",
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  leftContent: {
    flexDirection: 'row',
  },
  userIcon: {
    width: 50,
    height: 50,
    borderRadius: 100,
    marginRight: 5,
  },
  username: {},
  messageContent: {
    fontSize: 13,
    opacity: 0.7,
  },
  rightContent: {
    alignItems: 'center',
  },
  messageDate: {
    fontSize: 11,
  },
  unread: {
    backgroundColor: "#2f95dc",
    borderRadius: 100,
    width: 15,
    height: 15,
    textAlign: 'center',
  },
});
