import { Component, createRef } from "react";
import {
  ScrollView,
  View,
  StyleSheet,
  StatusBar,
  Pressable,
  TouchableOpacity
} from "react-native";
import {
  AntDesign,
  Ionicons,
  Feather,
  FontAwesome,
  MaterialIcons,
  Fontisto,
} from "@expo/vector-icons";
import { Cipher, Decipher } from "../../middleware/crypto";
import { Text } from "./Themed";
import Modal from "react-native-modal";

class MessagesContainer extends Component {
  private scrollViewRef = createRef();

  constructor(props) {
    super(props);
    this.state = {
      visible: false,
      scrollOffset: null,
    };

    this.user = props.user;
    this.chats = props.chats;
    this.updateChats = props.updateChats;
    this.chat = props.chat;
    this.colors = props.colors;
  }

  handleOnScroll(event) {
    this.setState({ scrollOffset: event.nativeEvent.contentOffset.y });
  }

  handleScrollTo(p) {}

  open = () => this.setState({ visible: true } as any);
  close = () => this.setState({ visible: false } as any);
  isVisible = () => this.state.visible;

  ModalMessage() {
    return (
      <Modal
        testID={"ModalMessage"}
        isVisible={this.isVisible()}
        onSwipeComplete={() => this.close()}
        swipeDirection={["down"]}
        scrollTo={this.handleScrollTo}
        scrollOffset={this.state.scrollOffset}
        scrollOffsetMax={400 - 300}
        propagateSwipe={true}
        style={styles.modal}
      >
        <View style={styles.scrollableModal}>
          <ScrollView onScroll={this.handleOnScroll} scrollEventThrottle={16}>
            <View style={styles.modalBarContainer}>
              <View style={styles.modalBar}></View>
            </View>

            <View
              style={[
                styles.modalContent,
                {
                  backgroundColor:
                    this.colors.theme === "dark"
                      ? this.colors.defaultColors.card
                      : this.colors.defaultColors.background,
                },
              ]}
            >
              <View style={styles.modalLinks}>
                <TouchableOpacity style={styles.modalLink}>
                  <FontAwesome
                    name="user-circle"
                    size={26}
                    color={this.colors.tint}
                  />
                  <Text style={styles.modalLinkText}>Perfil</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.modalLink}>
                  <AntDesign name="star" size={26} color={this.colors.tint} />
                  <Text style={styles.modalLinkText}>Fixar</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.modalLink}>
                  <MaterialIcons
                    name="mark-chat-read"
                    size={26}
                    color={this.colors.tint}
                  />
                  <Text style={styles.modalLinkText}>Marcar como lida</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.modalLink}>
                  <MaterialIcons
                    name="notification-important"
                    size={26}
                    color={this.colors.tint}
                  />
                  <Text style={styles.modalLinkText}>
                    Config. de notificação
                  </Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.modalLink} onPress={() => this.executeAction("removeChat", this.chat)}>
                  <MaterialIcons
                    name="delete"
                    size={26}
                    color={this.colors.tint}
                  />
                  <Text style={styles.modalLinkText}>Excluir conversa</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.modalLink}>
                  <Entypo name="block" size={26} color={this.colors.tint} />
                  <Text style={styles.modalLinkText}>Bloquear usuário</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.modalLink}>
                  <MaterialIcons
                    name="report"
                    size={26}
                    color={this.colors.tint}
                  />
                  <Text style={styles.modalLinkText}>Denunciar usuário</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.modalLink}>
                  <Ionicons name="copy" size={26} color={this.colors.tint} />
                  <Text style={styles.modalLinkText}>Copiar ID</Text>
                </TouchableOpacity>
              </View>
            </View>
          </ScrollView>
        </View>
      </Modal>
    );
  }

  render() {
    return (
      <ScrollView
        style={styles.messageContainer}
        ref={this.scrollViewRef}
        showsVerticalScrollIndicator={false}
        showsHorizontalScrollIndicator={false}
        onContentSizeChange={() =>
          this.scrollViewRef.current.scrollToEnd({ animated: true })
        }
      >
        {this.ModalMessage()}
        {this.chat.messages.map((message, index) => {
          const itsMyMessage =
            message.author.id === this.user.id ? true : false;
          let content = this.chat.key
            ? Decipher(message.content, this.chat.key)
            : "Nova mensagem";
          const messageTime = new Date(message.createdAt).toLocaleTimeString();

          let previousMessageIsMy =
            this.chat.messages[index - 1]?.author.id === message.author.id;
          let nextMessageIsMy =
            this.chat.messages[index + 1]?.author.id === message.author.id;

          let borders = {};

          if (itsMyMessage) {
            borders.borderTopRightRadius =
              previousMessageIsMy && !nextMessageIsMy ? 5 : 20;
            borders.borderBottomRightRadius =
              !previousMessageIsMy && nextMessageIsMy ? 5 : 20;

            if (previousMessageIsMy && nextMessageIsMy) {
              borders.borderTopRightRadius = 5;
              borders.borderBottomRightRadius = 5;
            }
          } else {
            borders.borderTopLeftRadius =
              previousMessageIsMy && !nextMessageIsMy ? 5 : 20;
            borders.borderBottomLeftRadius =
              !previousMessageIsMy && nextMessageIsMy ? 5 : 20;

            if (previousMessageIsMy && nextMessageIsMy) {
              borders.borderTopLeftRadius = 10;
              borders.borderBottomLeftRadius = 10;
            }
          }

          return (
            <View
              style={[
                itsMyMessage ? styles.myMessage : styles.otherMessage,
                index === 0 ? { margintop: 10 } : {},
              ]}
              key={index}
            >
              <View style={itsMyMessage ? { maxWidth: "75%" } : {}}>
                <Pressable
                  style={[
                    styles.message,
                    borders,
                    {
                      backgroundColor: itsMyMessage
                        ? "#2f95dc"
                        : this.colors.defaultColors.card,
                    },
                  ]}
                  onPress={() => this.open()}
                >
                  <Text
                    style={{
                      maxWidth: "100%",
                      color: itsMyMessage
                        ? "white"
                        : this.colors.defaultColors.text,
                    }}
                  >
                    {content}
                  </Text>
                </Pressable>
                <View
                  style={[
                    styles.messageProps,
                    {
                      justifyContent: itsMyMessage ? "flex-end" : "flex-start",
                    },
                  ]}
                >
                  {nextMessageIsMy ? (
                    <></>
                  ) : (
                    <>
                      <Text style={styles.messageCreatedAt}>
                        {messageTime.slice(0, 5)}
                      </Text>
                      {itsMyMessage ? (
                        <Ionicons
                          name="checkmark-done"
                          size={20}
                          color={message.read ? "#04f500" : "gray"}
                          style={{ marginRight: 2 }}
                        />
                      ) : (
                        <></>
                      )}
                    </>
                  )}
                </View>
              </View>
            </View>
          );
        })}
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  messageContainer: {
    flex: 1,
    marginHorizontal: 10,
    flexDirection: "column-reverse",
    marginBottom: 55,
  },
  myMessage: {
    justifyContent: "flex-end",
    flexDirection: "revert",
  },
  otherMessage: {
    width: "fit-content",
    maxWidth: "75%",
  },
  message: {
    padding: 7,
    borderRadius: 20,
    flexDirection: "row",
    marginBottom: 2,
    alignItems: "baseline",
    flexWrap: "wrap",
  },
  messageCreatedAt: {
    fontSize: 10,
    marginHorizontal: 5,
    marginTop: 5,
  },
  messageProps: {
    flexDirection: "row",
    alignItems: "center",
  },
  modal: {
    justifyContent: "flex-end",
    margin: 0,
  },
  scrollableModal: {
    minHeight: 500,
  },
  modalContent: {
    height: 500,
    padding: 10,
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
  },
  modalTopProfile: {
    flexDirection: "row",
    alignItems: "center",
    padding: 10,
    borderRadius: 50,
  },
  modalLinks: {},
  modalLink: {
    padding: 10,
    flexDirection: "row",
    alignItems: "center",
  },
  modalLinkText: {
    marginLeft: 10,
    fontSize: 17,
  },
  modalBarContainer: {
    justifyContent: "center",
    alignItems: "center",
  },
  modalBar: {
    backgroundColor: "#fff",
    width: 60,
    height: 10,
    borderRadius: 10,
    marginBottom: 5,
  },
});

export default MessagesContainer;
