import io from "socket.io-client";
import * as dotenv from "dotenv";
import * as uuid from "uuid";
dotenv.config();

export default class SocketController {
  constructor(socket) {
    this.socket = socket;
    console.log(this.socket);
  }

  public sendHalkMessage() {
    const timeNow = new Date().getTime();
    const halkUser = {
      id: "0",
      phone: "",
      profile: {
        name: "Halk",
        username: "Halk",
        avatar: require("../../assets/images/icon.png"),
        bio: "Official account of the team Halk",
      },
    };

    const halkChat = {
      id: "a384b2bc-1a3b-49d3-bdf5-1be72350c494",
      user: halkUser,
      key: generateKey(),
      messages: [
        {
          author: halkUser,
          content: "Welcome to halk!",
          createdAt: timeNow,
          id: `${timeNow}${Math.floor(
            Math.random() * (100000000 - 1000000 + 1) + 1000000
          )}`,
          read: false,
        },
      ],
    };

    return halkChat;
  }
}

export function CreateSocketConnection({ userId }) {
  const socket = io("http://localhost:3000", {
    transports: ["websocket"],
    auth: {
      userId,
    },
  });

  new SocketController(socket);
  return socket;
}
